muclient.script:
function OnPluginBroadcast (msg, id, name, text)
-- state change if id == "aaa79afcb20fa11787c5a327" then oldstatus = status status = GetPluginVariableList("aaa79afcb20fa11787c5a327") if status.statestring == 'active' then checkstand() end elseif id == '3e7dedbe37e44942dd46d264' then if text:find("room.info") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","room") room = assert (loadstring ('return ' .. gmcparg or ""))() current_room = {} current_room['id'] = tonumber(room.info.num) current_room['name'] = room.info.name oldregen = verify_bool(phelper.inregen) phelper:set('inregen', check_room(current_room.id), {silent=true})
if oldregen and verify_bool(phelper.inregen) then return end
if verify_bool(phelper.inregen) and ((not verify_bool(phelper.regenworn)) or tryremove) and not runto then wear_regen() return end
if (not verify_bool(phelper.inregen)) and (verify_bool(phelper.regenworn) or trywear) then remove_regen() return end
end elseif id == "aaa7b6edb20fa44565c5a327" then if msg == 1 then runto = true elseif msg == 2 then runto = false end elseif id == "aaa7dc9ed92ffc6146964abc" then if msg == 2 then loadstring(text)() eqitem = item if eqitem.placestr == "lfinger" then if eqitem.name == "Aardwolf Ring of Regeneration" then phelper:set('regenworn', true, {silent=true}) phelper:set('regensn', eqitem.objectid, {silent=trie}) trywear = false SaveState() end end elseif msg == 1 then loadstring(text)() eqitem = item if eqitem.placestr == "lfinger" then if eqitem.name ~= "Aardwolf Ring of Regeneration" then phelper:set('oldringsn', eqitem.objectid, {silent=true}) phelper:set('oldringdesc', eqitem.name, {silent=true}) SaveState() elseif eqitem.name == "Aardwolf Ring of Regeneration" then phelper:set('regenworn', false, {silent=true}) tryremove = false SaveState() end end end end phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
assert (loadstring (GetVariable ("regenrooms") or "")) ()
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable ()
ldplugin ("broadcast_runto") ldplugin ("aard_GMCP_handler", "3e7dedbe37e44942dd46d264") ldplugin ("broadcast_state") ldplugin ("broadcast_invmon")
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState () SetVariable ("regenrooms", "regenrooms = " .. serialize.save_simple (regenrooms))
phelper:OnPluginSaveState() end -- function OnPluginSaveState
