muclient.script:
function OnPluginBroadcast (msg, id, name, text) if id == "aaa8a9eda20fa11787c6b438" and (msg == 6 or msg == 8) then DoAfterSpecial(.1 , 'formatquest()', 12) elseif id == "aaa0ab9ed92f8fc614664bd1" and msg == 1 then local pvar = GetPluginVariable( "aaa0ab9ed92f8fc614664bd1", 'levelinfo' )
-- get the info loadstring( pvar )() local info = levelinfo if info.type == 'level' then DoAfterSpecial(.1, 'formatlevel()', 12) else DoAfterSpecial(.1, 'formatpup()', 12) end elseif id == "aaa66f81c50828bbbfda7100" and (msg == 3 or msg == 4) then DoAfterSpecial(.1, 'formatcp()', 12) elseif id == "aaa77f81c5408278ccda7100" and msg == 4 then DoAfterSpecial(.1, 'formatgq()', 12) end
phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin("StatDB") ldplugin('broadcast_quest') ldplugin('broadcast_level') ldplugin('broadcast_cp') ldplugin('broadcast_gq')
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
