function OnPluginBroadcast (msg, id, name, text) if id == '3e7dedbe37e44942dd46d264' then if text:find("char") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","char") char = assert (loadstring ('return ' .. gmcparg or ""))() if char.status then state = char.status.state statestring = statestrings[tonumber(state)] if statestring == 'active' and waitcreated then waitcreated = false broadcast(2) end end end end phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
assert (loadstring (GetVariable ("levelinfo") or "")) ()
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () phelper:OnPluginEnable()
ldplugin ("aard_GMCP_handler", "3e7dedbe37e44942dd46d264")
if IsConnected () then CallPlugin("3e7dedbe37e44942dd46d264","Send_GMCP_Packet","request char") end -- if already connected end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
function OnPluginCommand (stext) stext = trim(stext) if stext == 'superhero loud' or stext == 'superhero silent' or stext == 'superhero confirm' then local wildcards = {} wildcards.newlevel = 201 level ( 'level', 'You raise a level! You are now level 201.', wildcards ) end end
