muclient.script:
internalrevision = "$Rev$"
require "var" require "pluginhelper" require "ldplugin" require "verify"
status = {}

function OnPluginBroadcast (msg, id, name, text) if id == "aaa79afcb20fa11787c5a327" then status = GetPluginVariableList("aaa79afcb20fa11787c5a327") end -- if if id == '3e7dedbe37e44942dd46d264' then if text:find("comm.tick") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","comm.event") stuff = assert (loadstring ('return ' .. gmcparg or ""))()
phelper:broadcast(1) if verify_bool(phelper.crontick) and (status.statestring == 'active' or status.statestring == 'sleeping' or status.statestring == 'resting') then phelper:mdebug("Sending", "cr") SendNoEcho("") end end end phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
