muclient.script:
require 'var' require "commas" require 'miniwin' require 'tprint' require 'pluginhelper' require 'ldplugin'
cpwin = Miniwin{name="CP"} cpwin:set('windowpos', 7) cpwin:set('font_size', 7) cpwin:set('font', 'Verdana') cpwin:add_setting("time_colour", {help="the colour for the time", type="colour", default=verify_colour(0xD670DA), sortlev=1})
cpmobs = {} cptimer = {}
function show_cp_text ()
local texttable = {}
local style = {}
-- do nothing if no campaign if #cpmobs == 0 or not next(cptimer) then return end -- if
-- heading style = {} style.text = "Current campaign. You need to kill: " style.len = #style.text style.style = 0 table.insert (texttable, {style})
-- list of mobs for i, v in ipairs (cpmobs) do style = {} style.text = v.name .. " (" .. v.room .. ")" style.len = #style.text style.style = 0 table.insert (texttable, {style}) end -- for
style = {} style.text = string.format ("Time to go: %sd %sh %sm", cptimer.days, cptimer.hours, cptimer.mins) style.len = #style.text style.textcolour = "time_colour" style.style = 0 table.insert (texttable, {style})
cpwin:enable() cpwin:createwin(texttable)
end -- show_cp_text

function OnPluginBroadcast (msg, id, name, text) if id == "aaa66f81c50828bbbfda7100" then if msg == 1 then local pvar = GetPluginVariable( "aaa66f81c50828bbbfda7100", "mobs" )
-- get the mobs loadstring( pvar )() cpmobs = mobs show_cp_text() elseif msg == 2 then local pvar = GetPluginVariable( "aaa66f81c50828bbbfda7100", "timer" )
-- get the timer loadstring( pvar )() cptimer = timer show_cp_text() elseif msg == 3 or msg == 4 then cptimer = {} mobs = {} cpwin:disable() end
end -- cp broadcast PluginhelperOnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
PluginhelperOnPluginInstall() end -- OnPluginInstall
function OnPluginClose()
PluginhelperOnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin("broadcast_cp")
PluginhelperOnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
PluginhelperOnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
PluginhelperOnPluginConnect() end -- OnPluginConnect
function OnPluginDisconnect ()
PluginhelperOnPluginDisonnect() end -- function OnPluginConnect
function OnPluginSaveState ()
PluginhelperOnPluginSaveState() end -- function OnPluginSaveState
