muclient.script:
function OnPluginBroadcast (msg, id, name, text) if id == "aaa66f81c50828bbbfda7100" then if msg == 1 then local pvar = GetPluginVariable( "aaa66f81c50828bbbfda7100", "mobs" )
-- get the mobs loadstring( pvar )() cpmobs = mobs
local pvar2 = GetPluginVariable( "aaa66f81c50828bbbfda7100", "cp_info") loadstring(pvar2)() level = convertlevel(cp_info.level)
show_cp_text() elseif msg == 2 then local pvar = GetPluginVariable( "aaa66f81c50828bbbfda7100", "timer" )
-- get the timer loadstring( pvar )() cptimer = timer show_cp_text() elseif msg == 3 or msg == 4 then cptimer = {} mobs = {} cpwin:disable() end
elseif id == "8a710e0783b431c06d61a54c" then if msg == 1 then -- get one variable tcurlevel = GetPluginVariable ("8a710e0783b431c06d61a54c", "level") if curlevel ~= tcurlevel then curlevel = tcurlevel show_cp_text() end end end -- stats changed
phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin("broadcast_cp") ldplugin("Stats_Detector")
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
