muclient.script:
require 'var' require "commas" require 'miniwin' require 'tprint' require 'pluginhelper' require 'ldplugin'
gqwin = Miniwin:new{name="GQ"} gqwin:set_default('windowpos', 7) gqwin:set_default('header_height', 0) --gqwin:disable()
gqmobs = {} gq_text = {}
function build_gq ()
local texttable = {}
local style = {}
-- do nothing if no gquest if #gqmobs == 0 then return end -- if
-- heading style = {} style.text = "GQuest: You need to kill: " style.len = #style.text style.style = 0 table.insert (texttable, {style})
-- list of mobs for i, v in ipairs (gqmobs) do style = {} style.text = v.num .. " : " .. v.name .. " (" .. v.room .. ")" style.len = #style.text style.style = 0 table.insert (texttable, {style}) end -- for
gq_text = texttable gqwin.header_height = 1 show_gq()
end -- build_gq
function show_gq () -- do nothing if no gq if #gq_text == 0 then return end -- if
gqwin:enable() gqwin:createwin(gq_text) gqwin:show(true) end -- show_quest_text

function OnPluginBroadcast (msg, id, name, text) if id == "aaa77f81c5408278ccda7100" then if msg == 1 then -- gquest available gqwin.header_height = 0 gq_text = {"GQuest Available"} show_gq() elseif msg == 2 then print("gquest joined") -- gquest joined gqwin.header_height = 0 gq_text = {"GQuest Joined - Waiting"} show_gq() elseif msg == 3 then local pvar = GetPluginVariable( "aaa77f81c5408278ccda7100", "mobs" )
-- get the mobs loadstring( pvar )() gqmobs = mobs build_gq() elseif msg == 4 or msg == 5 then -- reward gqwin:disable() end
end -- gq broadcast phelper:OnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin("broadcast_gq")
phelper:OnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
