function OnPluginBroadcast (msg, id, name, text) --local windowstuff = {} if id == GetPluginID() then return end if msg == 5000 then loadstring( text )() local winstuff = windowstuff if next(winstuff) then tabwin:enable() tabwin:addtab(winstuff) end elseif msg == 5001 then loadstring( text )() local winstuff = windowstuff if next(winstuff) then tabwin:removetab(winstuff) end end PluginhelperOnPluginBroadcast(msg, id, name, text) end
function OnPluginInstall()
PluginhelperOnPluginInstall() end -- OnPluginInstall
function OnPluginClose()
PluginhelperOnPluginClose() end -- OnPluginClose
function OnPluginEnable ()
PluginhelperOnPluginEnable() end -- OnPluginEnable
function OnPluginDisable ()
PluginhelperOnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
PluginhelperOnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
PluginhelperOnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState()
PluginhelperOnPluginSaveState(options_table) end
