muclient.script:
function moonbot_union(name, line, wildcards) print(nextunion()) return true end -- moonbot_union
function moonbot_reset() for i,v in pairs(moons) do moons[i].now = nil end countdown = nil countdowndur = nil moonwin:disable() ColourNote("", "", "") ColourNote( "orange", "black", "Moonbot reset" ) ColourNote("", "", "") end -- moonbot_reset
set_options = { plotlength = {help="set the length of the moon plot", type="number", high=80, low=0, after=styleplotdata, default=66}, plugin_colour = {help="set the plugin colour", type="colour", default="lime"}, tickgag = {help="toggle gagging the tick", type="bool", after=check_tickgag,default=false}, shortcmd = {help="the short command for this plugin", type="string", after=set_plugin_alias, default="mb"}, longcmd = {help="the long command for this plugin", type="string", after=set_plugin_alias, default="moonbot"}, }
plugin_options = { plot = {func=plotdata, help="plot moons"}, union = {func=moonbot_union, help="show next moons union"}, print = {func=printmoons, help="print the current moon phase #s"}, toggle = {func=togglewindow, help="toggle the moons miniwindow"}, reset = {func=moonbot_reset, help="reset the moons"}, }
function plugin_parse (name, line, wildcards) plugin_parse_helper(name, line, wildcards, plugin_options, set_options, moonwin) end -- plugin_parse
init_plugin_vars(set_options) set_plugin_alias()
